import { Component } from '@angular/core';
import { DataService } from '../services/data.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-upload-transcript',
  templateUrl: './upload-transcript.component.html',
  styleUrls: ['./upload-transcript.component.scss']
})
export class UploadTranscriptComponent {
 // Variable to store shortLink from api response
 shortLink: string = "";
 loading: boolean = false; // Flag variable
 file!: File ; // Variable to store file

 // Inject service
 constructor(private fileUploadService: DataService, private router:Router) { }

 ngOnInit(): void {
 }

 // On file Select
 onChange(event:any) {
     this.file = event.target.files[0];
 }

 // OnClick of button Upload
 onUpload(event : Event) {
     this.loading = !this.loading;
     console.log(this.file);
     this.fileUploadService.upload(this.file).subscribe(
        {
          next:(response)=>console.log(response),
          error:(err)=>console.log(err),
          complete:()=>{
            alert('Successfully Uploaded');
          this.router.navigateByUrl('')
          }

        }
     );
     event.preventDefault();
 }
  }




<div class="relative">
    <div class="flex items-center justify-center my-4">
      <div class="bg-gradient-to-r from-pink-500 via-red-500 to-yellow-500 p-1">
        <div
          class="flex items-center justify-center px-4 py-10 bg-white sm:px-6 lg:px-8 sm:py-16 lg:py-8">
          <div class="xl:w-full xl:max-w-sm 2xl:max-w-md xl:mx-auto lg:w-[24rem]">
            <h2 class="text-2xl text-center font-semibold leading-tight text-black sm:text-4xl">
              Upload Transcript
            </h2>
            <form  class="mt-8"  >
              <div class="space-y-5">
                <div>
                  <div class="mt-2.5">
                    <input
                    #fileInput
                    (change)="onChange($event)"
                     class="block py-2.5 px-2.5 w-full text-sm text-gray-900 border border-gray-300 rounded-lg cursor-pointer bg-gray-50 dark:text-gray-400 focus:outline-none dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400" id="file_input" type="file">

                  </div>
                </div>

                <div>
                  <!-- <button
                    class="w-full inline-flex items-center justify-center rounded-md bg-indigo-600 px-3.5 py-2.5 text-base mb-4 font-semibold leading-7 text-white hover:bg-indigo-500"

                    >
                    Get Transcript
                    <svg
                      xmlns="http://www.w3.org/2000/svg"
                      fill="none"
                      viewBox="0 0 24 24"
                      stroke-width="1.5"
                      stroke="currentColor"
                      class="w-4 h-4 ml-2">
                      <path
                        stroke-linecap="round"
                        stroke-linejoin="round"
                        d="M4.5 12h15m0 0l-6.75-6.75M19.5 12l-6.75 6.75"></path>
                    </svg>
                  </button> -->
                  <button
                  class="w-full inline-flex items-center justify-center rounded-md bg-green-600 px-3.5 py-2.5 text-base font-semibold leading-7 text-white hover:bg-indigo-500"
                  (click)="onUpload($event)">
                  Upload Transcript
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    fill="none"
                    viewBox="0 0 24 24"
                    stroke-width="1.5"
                    stroke="currentColor"
                    class="w-4 h-4 ml-2">
                    <path
                      stroke-linecap="round"
                      stroke-linejoin="round"
                      d="M4.5 12h15m0 0l-6.75-6.75M19.5 12l-6.75 6.75"></path>
                  </svg>
                </button>
                </div>
              </div>
            </form>

          </div>
        </div>
      </div>
    </div>
  </div>
